noble.scss:
/* Common Style Start */
$theme_primary: stencilColor("button--primary-backgroundColor");

@mixin compareButtonCss {
	position: absolute;
	left: 50%;
	top: 50%;
	right: auto;
	bottom: auto;
	transform: translate(-50%, -50%);
    -webkit-transform: translate(-50%, -50%);
    -moz-transform: translate(-50%, -50%);
    -ms-transform: translate(-50%, -50%);
    -o-transform: translate(-50%, -50%);
    width: 100%;
    height: 100%;
    border: none; 
    outline: none;
    -webkit-box-shadow: none; 
    -moz-box-shadow: none;
    box-shadow: none;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
    background: none;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    cursor: pointer;
    ~ .icon {
		svg {
			&:first-child {
				display: none;
			}
			&:last-child {
				display: block;
			}
		}
		&.not-compared {
			svg {
				&:first-child {
					display: block;
				}
				&:last-child {
					display: none;
				}
			}
		}
	}
}

input:focus, 
button:focus, 
textarea:focus, 
select:focus, 
details:focus, 
[href]:focus, 
[tabindex]:not([tabindex="-1"]):focus, 
[contenteditable=true]:focus {
	outline-color: stencilColor("focus-color") !important;
}

.hide-outline{
	input:focus,
	button:focus, 
	textarea:focus, 
	select:focus, 
	details:focus, 
	[href]:focus, 
	[tabindex]:not([tabindex="-1"]):focus, 
	[contenteditable=true]:focus {
		outline: none !important;
	}

	input:focus + label {
		outline: none !important;
	}
}

.blank-checkbox-label{
	opacity: 0;
	&:hover{
		cursor: default;
	}
}


.page-heading {
	text-align: left;
	font-family: stencilFontFamily("headings-font");
	font-weight: stencilFontWeight("headings-font");
	font-size: stencilNumber("fontSize-heading") - 12;
	@include breakpoint("small"){
		font-size: stencilNumber("fontSize-heading") - 6;
	}
	@include breakpoint("medium"){
		font-size: stencilNumber("fontSize-heading") - 4;
	}
	@include breakpoint("large"){
		font-size: stencilNumber("fontSize-heading");
	}
}

.hp-section-title{
	font-family: stencilFontFamily("section-main-font");
	font-weight: stencilFontWeight("section-main-font");
	font-size: stencilNumber("section-main-fontsize") - 12;
	line-height: 1.2;
	color: stencilColor("section-main-color");
	/*text-transform: uppercase;*/
	@include breakpoint("small"){
		font-size: stencilNumber("section-main-fontsize") - 6;
	}
	@include breakpoint("medium"){
		font-size: stencilNumber("section-main-fontsize") - 4;
	}
	@include breakpoint("large"){
		font-size: stencilNumber("section-main-fontsize");
	}
}
.hp-section-subtitle{
	font-family: stencilFontFamily("section-sub-font");
	font-weight: stencilFontWeight("section-sub-font");
	font-size: stencilNumber("section-sub-fontsize") - 2;
	line-height: 1.2;
	color: stencilColor("section-sub-color");
	/*text-transform: uppercase;*/
	@include breakpoint("medium"){
		font-size: stencilNumber("section-sub-fontsize") - 1;
	}
	@include breakpoint("large"){
		font-size: stencilNumber("section-sub-fontsize");
	}
}
.hp-section-main-desc{
	font-family: stencilFontFamily("body-font");
	font-weight: stencilFontWeight("body-font");
	font-size: $fontSize-root;
	color: stencilColor("color-textBase");
	@include breakpoint("medium"){
		font-size: $fontSize-root + 2;
	}
	@include breakpoint("large"){
		font-size: $fontSize-root + 4;
	}
}
.hp-section-sub-desc{
	font-family: stencilFontFamily("body-font");
	font-weight: stencilFontWeight("body-font");
	font-size: $fontSize-root - 2;
	font-style: italic;
	color: stencilColor("color-textBase");
	@include breakpoint("medium"){
		font-size: $fontSize-root;
	}
	@include breakpoint("large"){
		font-size: $fontSize-root + 2;
	}
}
.hp-section-link{
	font-family: stencilFontFamily("section-link-font");
	font-weight: stencilFontWeight("section-link-font");
	font-size: $fontSize-root - 2;
	color: stencilColor("section-link-color");
	text-decoration: underline;
	text-transform: uppercase;
	letter-spacing: 1px;
	&:hover {
		color: stencilColor("section-link-hover-color");
	}
	@include breakpoint("small") {
		font-size: $fontSize-root - 1;
	}
	@include breakpoint("medium") {
		font-size: $fontSize-root;
	}
}

.modal-close{
	svg{
		fill: #545454;
		width: 30px;
		height: 30px;
		@include breakpoint("medium"){
			width: 40px;
			height: 40px;
		}
	}
}

.category--template, .search--template, .brand--template{
	.breadcrumbs{
		display: none;
	}
}

.default--template {
	&.hide-top-banner {
		@media screen and (max-width: $screen-medium) {
			padding-top: 15px;
		}
	}
	.body{
		margin-top: 0;
	}
}

.alertBox--info{
	svg{
		fill: stencilColor("alert-color-alt");
	}
}

.alertBox {
	border-radius: 0;
}

.form-label--alternate,.form-label{
	color: stencilColor("form-label-font-color");
}
.form-select{
	background-color: stencilColor("input-bg-color");
}

/* Common Style End */

/* Header Style Start */
.header-logo-image-container{
	@media screen and (max-width: 800px) {
		max-height: 100%;
	}
}
.header-logo-image{
	@media screen and (max-width: 800px) {
		max-height: 60px;
	}
}
/* Header Style End */

/* Global Top Banner Style Start */
.global-banner {
	padding: 12px 15px;
	@include breakpoint("medium") {
		position: static;
		padding: 14px;
	}
	.global-banner-text{
		font-size: $fontSize-root - 1;
		letter-spacing: 0.25px;
		line-height: 1.2;
		text-align: center;
		/*text-transform: uppercase;*/
		margin: 0;
		@include breakpoint("medium"){
			font-size: $fontSize-root + 1;
		}
	}
	&.top {
		position: static;
		width: 100%;
		top: 0;
		z-index: 100;
		background-color: stencilColor("banner-bgcolor");
		.global-banner-text{
			font-family: stencilFontFamily("banner-font"), Arial, Helvetica, sans-serif;
			font-weight: stencilFontWeight("banner-font");
			color: stencilColor("banner-textcolor");
			animation-delay: 0.5s;
		}
	}
	&.bottom{
		background-color: stencilColor("bottom-banner-bgcolor");
		.global-banner-text{
			font-family: stencilFontFamily("bottom-banner-font"), Arial, Helvetica, sans-serif;
			font-weight: stencilFontWeight("bottom-banner-font");
			color: stencilColor("bottom-banner-textcolor");
		}
	}
}
/* Global Top Banner Style End */

/* HP Text Banner Style Start */
.hp-text-section {
	margin: 35px 0 30px;
	@include breakpoint("medium"){
		margin: 47px 0 56px;
	}
	.section-wrapper{
		text-align: center;
		@include breakpoint("medium"){
			max-width: 90%;
    		margin: 0 auto;
		}
		@include breakpoint("large"){
			max-width: 77%;
		}
		.hp-section-title{
			margin: 0;
		}
		.hp-section-main-desc{
			margin: 20px 0 15px;
			@include breakpoint("medium"){
				margin: 35px 0 15px;	
			}
		}
		p{
			&:last-child{
				margin-bottom: 0;
			}
		}
	}
}
/* HP Text Banner Style End */

/* HP Sidebyside Banner Style Start */
.hp-sidebyside-banner{
	margin: 0 0 120px;
	@include breakpoint("medium"){
		margin: 0 0 125px;
	}
	.banner-wrapper{
		@include breakpoint("medium"){
			display: flex;
		    align-items: flex-start;
		    justify-content: space-between;
		}
		&.boxed-width{
			max-width: calc(100% - 1.5rem);
			margin: 0 auto;
			@include breakpoint("large"){
				max-width: 85%;
				padding: 0 1rem;
			}
			@include breakpoint(1600px){
				max-width: 1440px;
			}
		}
		.banner-inner{
			margin-bottom: 90px;
			position: relative;
			@include breakpoint("medium"){
				margin-bottom: 0;
				width: calc(50% - 20px);
			}
			.banner-img{
				cursor: pointer;
				overflow: hidden;
				display: block;
				img{
					width: 100%;
					object-fit: cover;
					transition: all 0.5s ease;
				}
			}
			.banner-content{
				background-color: stencilColor("color-white");
				width: 100%;
				max-width: calc(100% - 60px);
				position: absolute;
				left: 50%;
				transform: translateX(-50%);
				bottom: -70px;
				margin: 0 auto;
				padding: 20px 15px;
				text-align: center;
				@include breakpoint("medium"){
					max-width: calc(100% - 80px);
					bottom: -50px;
				}
				@include breakpoint("large"){
					max-width: calc(100% - 128px);
				}
				.hp-section-title{
		    		font-size: 16px;
		    		margin: 0 0 10px;
		    		@include breakpoint("small"){
		    			font-size: 18px;
		    		}
		    		@include breakpoint("medium"){
		    			font-size: 20px;
		    		}
		    		@include breakpoint("large"){
		    			font-size: 28px;
		    		}
		    	}
		    	.hp-section-main-desc{
		    		margin: 0 0 10px;
		    	}
			}

			&.animation-enabled {
				&:hover, &:focus {
					.banner-img {
						img {
							transform: scale(1.1);
						}
					}
				}
			}
		}
	}
}
/* HP Sidebyside Banner Style End */

/* HP Shop Categories Banner Style Start */
.hp-categories-section {
	margin: 0 auto 30px;
	@include breakpoint("medium"){
		margin: 0 0 65px;
	}
	.container {
		padding: 0 1rem;
		@include breakpoint("large"){
			margin: 0 auto;
			max-width: 85%;
		}
		@include breakpoint(1600px){
			max-width: 1470px;
		}
	}
	.section-wrapper{
		.hp-section-title{
			text-align: center;
			margin: 0 0 25px;
			@include breakpoint("medium"){
				margin: 0 0 45px;
			}
		}
		.section-inner{
			display: flex;
			align-items: center;
			justify-content: space-between;
			flex-wrap: wrap;
			@include breakpoint("small"){
				margin: -10px;
			}
			@include breakpoint("large"){
				margin: -15px;
			}
			.section-content{
				width: 100%;
				position: relative;
				margin: 0 0 18px;
				@include breakpoint("small"){
					width: 50%;
					padding: 10px;
					margin: 0;
				}
				@include breakpoint("medium") {
					margin: 0;
					width: 25%;
					margin: 0 0 18px;
				}
				@include breakpoint("large") {
					padding: 15px;
				}
				&:first-child {
					margin-left: 0;
				}
				&:last-child {
					margin-right: 0;
				}
				.category-link {
					text-decoration: none;
					.category-link-inner {
						.section-img {
							overflow: hidden;
							img {
								width: 100%;
								object-fit: cover;
								transition: all 0.5s ease;
							}
						}
						.section-link {
							position: absolute;
							left: 50%;
							bottom: 35px;
							transform: translateX(-50%);
							text-transform: uppercase;
							text-align: center;
							box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.15);
							padding: 12px 25px;
							font-family: stencilFontFamily("section-link-font"), Arial, Helvetica, sans-serif;
							font-size: 14px;
							font-weight: $fontWeight-bold;
							color: stencilColor("button-text-color");
							background-color: stencilColor("button-background");
							transition: all 0.3s ease;

							&:hover, &:focus {
								background-color: stencilColor("button-hover-background");
								color: stencilColor("button-text-hover-color");
							}

							@include breakpoint("medium") {
								padding: 12px;
								font-size: 13px;
								min-width: 142px;
							}
							@include breakpoint("large") {
								bottom: 43px;
								padding: 15px;
								font-size: 14px;
							}
						}
					}
					&:hover {
						.category-link-inner {
							.section-link {
								background-color: stencilColor("button-hover-background");
								color: stencilColor("button-text-hover-color");
							}
						}
					}
					&.animation-enabled {
						&:hover {
							.category-link-inner {
								.section-img {
									img {
										-webkit-transform: scale(1.1);
										-moz-transform: scale(1.1);
										transform: scale(1.1);
									}
								}
							}
						}
					}
				}
			}
		}
	}
}
/* HP Shop Categories Banner Style End */

/* HP Horizontal Banner Style Start */
.hp-horizontal-banner{
	margin: 0 0 40px;
	@include breakpoint("medium"){
		margin: 0 0 40px;
	}
	.container{
		padding: 0 1rem;
		@include breakpoint("large"){
			margin: 0 auto;
			max-width: 85%;
		}
		@include breakpoint(1600px){
			max-width: 1440px;
		}
	}
	&.right-side-banner{
		@include breakpoint("medium"){
			margin: 0 0 70px;
		}
		.banner-wrapper{
			@include breakpoint("medium"){
				justify-content: flex-end;
				margin-left: auto;
			}
			.banner-content{
				@include breakpoint("medium"){
					left: 0;
				}
			}
		}
	}
	&.left-side-banner{
		.banner-wrapper{
			@include breakpoint("medium"){
				justify-content: flex-start;
				margin-left: 0;
			}
			.banner-content{
				@include breakpoint("medium"){
					right: 0;
				}
			}
		}
	}
	.banner-wrapper{
		display: flex;
		flex-wrap: wrap;
		justify-content: center;
		@include breakpoint("medium"){
    		position: relative;
    		justify-content: initial;
		}
		@include breakpoint("large"){
			max-width: calc(100% - 100px);
		}
		@include breakpoint(1600px){
			max-width: calc(100% - 142px);
		}
		&.animation-enabled {
			&:hover, &:focus {
				.banner-img {
					img {
						transform: scale(1.1);
					}
				}
			}
		}
		.banner-content{
			background: stencilColor("color-white");
			margin: 15px 0 0;
			text-align: center;
			@include breakpoint("medium"){
				padding: 20px;
				margin-top: 0;
				text-align: left;
			}
			@include breakpoint("large"){
				padding: 30px;
			}
			@include breakpoint("medium"){
				position: absolute;
				top: 50%;
				transform: translateY(-50%);
				width: 450px;
			}
			@include breakpoint(992px){
				max-width: 633px;
				width: 100%;
			}
			@include breakpoint(1200px){
				max-width: 633px;
				width: 100%;
			}
			@include breakpoint(1600px){
				max-width: 633px;
				width: 100%;
			}
			.hp-section-subtitle{
				display: block;
				margin: 0 0 11px;
			}
			.hp-section-title{
				margin: 0;
			}
			.hp-section-main-desc{
				margin: 10px 0 20px;

				@include breakpoint("medium"){
					margin: 30px 0 40px;
				}
			}
			.banner-button{
				.button{
					margin: -1px 10px 0 0;
					width: 165px;
					padding: 15px;

					@include breakpoint("xxsmall") {
						margin-bottom: 5px;
						margin-top: 5px;
					}
				}
				&:last-child{
					margin: 0;
				}
			}
		}
		.banner-img {
			@include breakpoint("medium") {
				max-width: 57%;
			}

			a {
				display: block;
				overflow: hidden;
			}

			img {
				transition: all 0.5s ease;
			}
		}
	}

}
/* HP Horizontal Banner Style End */

/* HP Parallax Banner Style Start */
.hp-parallax-banner{
	@include breakpoint("large"){
		margin: 166px 0 0;
	}
	&.transparent-content-bg{
		.parallax-block{
			.banner-wrapper{
				.banner-content{
					background-color: transparent;
					.hp-section-subtitle,.hp-section-title,.hp-section-main-desc{
						color: stencilColor("color-white");
					}
				}
			}
		}
	}
	.parallax-block{
		height: auto;
	    -webkit-align-items: center;
	    align-items: center;
	    display: -webkit-flex;
	    display: -ms-flexbox;
	    display: flex;
	    -ms-flex-align: center;
	    -webkit-flex-wrap: wrap;
	    -ms-flex-wrap: wrap;
	    flex-wrap: wrap;
	    margin: 0;
		padding: 70px 0;
		position: relative;
		width: 100%;

		@include breakpoint("small") {
			padding: 140px 0;
		}
		.banner-wrapper{
			width: auto;
			display: flex;
			-webkit-align-items: center;
	    	align-items: center;
			margin: auto 0;
			position: relative;
			z-index: 9;

			.banner-content{
				padding: 35px 28px;
				margin: auto 0;
				width: auto;
				height: auto;
				display: block;
				background: stencilColor("color-white");
				max-width: 250px;
				@include breakpoint("small") {
					max-width: 370px;
				}
				.hp-section-subtitle{
					display: block;
					font-size: stencilNumber("section-sub-fontsize");
					@include breakpoint("small"){
						font-size: stencilNumber("section-sub-fontsize") + 2;
					}
					@include breakpoint("medium"){
						font-size: stencilNumber("section-sub-fontsize") + 4;
						margin: 0 0 6px;
					}
					@include breakpoint("large"){
						font-size: stencilNumber("section-sub-fontsize") + 6;
					}
				}
				.hp-section-title{
					margin: 0;
				}
				.hp-section-main-desc{
					margin: 15px 0 30px;
				}
			}
		}
		.parallax__container {
	    	clip: rect(0, auto, auto, 0);
	    	height: 100%;
	    	left: 0;
	    	overflow: hidden;
	    	position: absolute;
	    	top: 0;
	    	width: 100%;
	    	z-index: 1;

	    	.parallax {	
	    		background-position: center;
	    		background-repeat: no-repeat;
	    		background-size: cover;
	    		position: fixed;
	    		top: 0;
	    		-webkit-transform: translate3d(0,0,0);
	    		transform: translate3d(0,0,0);
	    		-webkit-transform-style: preserve-3d;
	    		transform-style: preserve-3d;
	    		width: 100%;

	    		a {
	    			display: block;
	    			height: 100%;
	    			width: 100%;
	    			position: absolute;
	    		}
			}
		}
	}
}
/* HP Parallax Banner Style End */

/* HP Recent Blog Style Start */
.hp-recent-blogs{
	margin: 40px 0 0;
	@include breakpoint("large"){
		margin: 85px 0 0;
	}
	.container{
		padding: 0 1rem;
		@include breakpoint("large"){
			padding: 0;
			margin: 0 auto;
			max-width: 85%;
		}
		@include breakpoint(1600px){
			max-width: 1440px;
		}
	}
	.blog-content{
		&.show-blog-img {
			.blog-grid {
				.blog-post {
					.blog-post-wrapper {
						.blog-img {
							display: block;
						}
					}
				}
			}
		}
		.hp-section-title{
			text-align: center;
			@include breakpoint("medium"){
				margin: 0 0 15px;
			}

			@include breakpoint("large"){
				margin: 0 0 35px;
			}
		}
		.blog-grid{
		    width: auto;
		    display: flex;
		    align-items: flex-start;
		    flex-direction: column;
		    margin: auto -14px;
		    @include breakpoint("medium") {
		    	flex-direction: row;
			}
			.blog-post{
				padding: 16px 15px;
			    text-align: center;
			    width: 100%;
			    margin-bottom: 20px;
			    &:last-child{
			    	margin-bottom: 0;
			    }
			    @include breakpoint("medium") {
			    	width: calc(100% / 3);
			    	margin-bottom: 0;
				}
				.blog-post-wrapper{
					.blog-img{
						// display: block;
						display: none;
	    				margin-bottom: 30px;
	    				overflow: hidden;

						img {
							width: 100%;
							object-fit: cover;
							transition: all 0.5s ease;
						}
					}
					.blog-details{
						.blog-date{
							margin: 0;
						    font-size: $fontSize-root;
						    color: stencilColor("section-sub-color");
						}
						.blog-title{
							@include breakpoint("medium"){
								margin: 0;
								line-height: 1.25;
							}

							@include breakpoint("large"){
								line-height: 1.5;
							}
						    a {
						    	text-decoration: none;
						    	font-family: stencilFontFamily("section-main-font");
								font-weight: stencilFontWeight("section-main-font");
							    font-size: stencilNumber("section-sub-fontsize") - 2;
							    color: stencilColor("blog-title-color");

								@include breakpoint("medium") {
									font-size: stencilNumber("section-sub-fontsize") + 4;
								}

								@include breakpoint("large") {
									font-size: stencilNumber("section-sub-fontsize") + 6;
								}

								&:hover, &:focus {
									color: stencilColor("blog-title-hover-color");
								}
						    }
						}
					}
				}
			}
		}
	}

	&.animation-enabled {
		.blog-post {
			&:hover, &:focus {
				.blog-post-wrapper {
					.blog-img {
						img {
							-webkit-transform: scale(1.1);
							-moz-transform: scale(1.1);
							transform: scale(1.1);
						}
					}
				}
			}
		}
	}
}
/* HP Recent Blog Style End */

/* HP Newsletter Popup Style Start */
#newsletter-popup {
	#dialog {
		position: fixed;
		z-index: 9999999999999999;
		width: 300px;
		height: auto;
		background-color: stencilColor("newsletterPopup-content-bgColor");
		display: block !important;
		border-radius: 0;
		left: auto !important;
		right: 10px !important;
	    bottom: 20px !important;
	    top: auto !important;
	    margin: 0 !important;
	    outline: 0;
	    overflow: hidden;
	    padding: 30px 25px 30px 25px;
	    -ms-transform: translate(0%, 0%);
	    transform: translate(0%, 0%);
	    text-align: center;
	    -webkit-box-shadow: 0px 0px 1px 1px rgba(stencilColor("color-white"), 0.05);
	    -moz-box-shadow: 0px 0px 1px 1px rgba(stencilColor("color-white"), 0.05);
	    box-shadow: 0px 0px 1px 1px rgba(stencilColor("color-white"), 0.05);
	    @include breakpoint("small") {
			width: 400px;
			right: 15px !important;
		}
		@include breakpoint("medium") {
			width: 450px;
			right: 15px !important;
		}
		@include breakpoint("large") {
			width: 475px;
			right: 20px !important;
		}
	}
	.form-prefixPostfix {
		.form-input {
			float: none;
			width: 100%;
			text-align: center;
			background: transparent;
			height: 49px;
		}

		.button {
			margin-top: 15px;
			float: none;
			width: 100%;
			border-radius: 0;
			background-color: stencilColor("newsletterPopup-button-bgColor");
			color: stencilColor("newsletterPopup-button-text-color");
			font-size: 16px;
			padding-top: 14p;
			padding-bottom: 14px;
			border: none;
			font-weight: $fontWeight-bold;
			letter-spacing: 1px;
			height: 49px;
			margin-left: 0;
			@include breakpoint("medium") {
				font-size: 18px;
			}
			@include breakpoint("large") {
				font-size: 22px;
			}
			&:hover, &:focus {
				color: stencilColor("newsletterPopup-button-text-hover-color");
				background-color: stencilColor("newsletterPopup-button-hover-bgColor");
			}
		}
	}

	.close {
		display: block;
		position: absolute;
		top: 9px;
		right: 9px;

		svg {
			width: 40px;
		    height: 40px;
		    stroke: stencilColor("newsletterPopup-text-color");

		    @include breakpoint("medium") {
		    	width: 50px;
		    	height: 50px;
		    }

		    @include breakpoint("large") {
		    	width: 70px;
		    	height: 70px;
		    }
		}
	}

	.popup-info-heading {
		/*text-transform: uppercase;*/
		font-family: $header-font-family;
	  	font-weight: $header-font-weight;
		font-size: 24px;
		color: stencilColor("newsletterPopup-text-color");
		margin-top: 15px;

		@include breakpoint("medium") {
			font-size: 30px;
		}

		@include breakpoint("large") {
			font-size: 36px;
		}
	}

	.subscription-intro {
		color: stencilColor("newsletterPopup-desc-color");
	}

	.form-field {
		margin-bottom: 15px;
	}

	input#nl_email {
		color: stencilColor("color-textSecondary");
		text-align: left;
		border: 1px solid stencilColor("color-textSecondary");
		font-size: 18px;
	}

	input#nl_email::placeholder {
		color: stencilColor("color-textSecondary");
		text-align: left;
		font-size: 18px;
	}

	input#nl_email::-webkit-input-placeholder { /* Chrome/Opera/Safari */
		color: stencilColor("color-textSecondary");
		text-align: left;
		font-size: 18px;
    }

    input#nl_email::-moz-placeholder { /* Firefox 19+ */
		color: stencilColor("color-textSecondary");
		text-align: left;
		font-size: 18px;
    }

    input#nl_email:-ms-input-placeholder { /* IE 10+ */
		color: stencilColor("color-textSecondary");
		text-align: left;
		font-size: 18px;
    }

    input#nl_email:-moz-placeholder { /* Firefox 18- */
		color: stencilColor("color-textSecondary");
		text-align: left;
		font-size: 18px;
    }

	#newsletter-wrapper {
	    margin-bottom: -25px;
	}

	.newsletter-popup-content {
		p {
			font-size: 14px;
			color: stencilColor("newsletterPopup-desc-color");
			margin-bottom: 20px;

			@include breakpoint("medium") {
				font-size: 15px;
				margin-bottom: 28px;
			}

			@include breakpoint("large") {
				font-size: 18px;
				margin-bottom: 42px;
			}
		}
	}

	&.popup-active-with-cookie {
		display: none !important;

		&.hide-popup {
		    display: none !important;
		}

		&.show-popup {
		    display: block !important;
		}
	}

	&.popup-active-on-refresh {
		&.hide-popup {
		    display: none !important;
		}

		&.show-popup {
		    display: block !important;
		}
	}
}
/* HP Newsletter Popup Style End */

/* Product Cards Style Start */
.hp-products-section {
	margin: 0 0 20px;
	overflow-y: hidden;

	@include breakpoint("medium"){
		margin: 0 0 60px;
	}
	@include breakpoint("large"){
		margin: 0 0 100px;
	}
	.container{
		padding: 0 1rem;
		@include breakpoint("large"){
			margin: 0 auto;
			max-width: 85%;
		}
		@include breakpoint(1600px){
			max-width: 1466px;
		}
	}
    .tabs {
    	padding: 0;
    	margin: 0 auto 5px;
	    text-align: center;
	    border: none;

	    @include breakpoint("medium") {
	    	margin: 0 auto 25px;
	    }

	    .tab {
	    	padding: 0;
		    float: none;
		    text-align: center;
		    border: none;
		    display: block;
		    margin: 0 0 10px;

		    @include breakpoint("small") {
		    	display: inline-block;
		    	padding: 0 25px;
		    	margin: 0;
			}

			@include breakpoint("medium") {
				padding: 0 36px;
			}

		    .tab-title {
		    	font-family: stencilFontFamily("section-main-font");
				font-weight: stencilFontWeight("section-main-font");
				font-size: stencilNumber("section-main-fontsize") - 12;
				line-height: 1.2;
				color: stencilColor("section-main-color");
				text-transform: inherit;
				padding: 4px 0;
			    margin: 0;
			    border-bottom: none;
			    letter-spacing: 0.25px;
			    display: inline-block;
			    position: relative;

			    &::after {
			    	content: "";
			    	position: absolute;
			    	left: 0;
			    	bottom: 0;
			    	height: 2px;
			    	width: 0%;
			    	background-color: stencilColor("section-main-color");
			    	transition: all 0.3s ease;
			    }

				@include breakpoint("medium"){
					font-size: stencilNumber("section-main-fontsize") - 4;
				}

				@include breakpoint("large"){
					font-size: stencilNumber("section-main-fontsize");
				}
		    }

		    &.is-active {
		    	margin: 0 0 10px;
		    	@include breakpoint("medium"){
		    		margin: 0;
		    	}
		    	.tab-title {
		    		&::after {
		    			width: 100%;
		    		}
		    	}
		    }
		}
    }
    .tabs-contents {
    	margin-bottom: 0;

    	.tab-content.has-jsContent {
    		// transform: translateY(20px);
    		// opacity: 0;
			transform: translateY(0px);
			opacity: 1;
    		transition: height 0.5s ease, visibility 0.5s ease, opacity 0.5s ease, transform 0.5s ease;

    		&.is-active {
    			padding-bottom: 0;
    			// transform: translateY(0px);
    			// opacity: 1;
    		}
    	}
    }
    .productGrid {
    	margin-bottom: 0;

    	&.slick-slider{
    		margin: auto;
    	}
    	.slick-next {
		    &::before {
		        @include carousel-arrow-color(stencilColor("arrow-color"), "next");
		    }

		    &:hover {
		        &::before {
		            @include carousel-arrow-color(stencilColor("arrow-hover-color"), "next");
		        }
		    }
		}
		.slick-prev {
		    &::before {
        		@include carousel-arrow-color(stencilColor("arrow-color"), "prev");
		    }

		    &:hover {
		        &::before {
            		@include carousel-arrow-color(stencilColor("arrow-hover-color"), "prev");
		        }
		    }
		}

		.product {
			padding: 0 19px;
		}
    }

    .hp-products:not(:last-child) {
    	margin-bottom: 50px;
    }

    .hp-section-title {
    	font-family: stencilFontFamily("section-main-font"), Arial, Helvetica, sans-serif;
		font-weight: stencilFontWeight("section-main-font");
		font-size: stencilNumber("section-main-fontsize") - 12;
		line-height: 1.2;
		color: stencilColor("section-main-color");
		/*text-transform: uppercase;*/
	    letter-spacing: 0.25px;
	    display: block;
	    position: relative;
    	margin: 0 auto 20px;
	    text-align: center;

	    @include breakpoint("medium") {
	    	margin: 0 auto 45px;
	    }

		@include breakpoint("medium") {
			font-size: stencilNumber("section-main-fontsize") - 4;
		}

		@include breakpoint("large") {
			font-size: stencilNumber("section-main-fontsize");
		}
    }
}

