.card-swatches{
	display: flex;
	align-items: center;
	justify-content: center;
	flex-wrap: wrap;
	margin: 5px 0 10px;

	.form-option {
		padding: 2px;
		text-align: center;
	    border: 1px solid #a6a6a6;
	    border-radius: 50%;
	    margin: auto 3px 3px;

	    /*&.form-option-multiswatch{
	    	border-radius: 5px;
	    }*/

		@include breakpoint("small") {
			margin: auto 2px 3px;
		}

		@include breakpoint(1601px) {
			margin: auto 4px 3px;
		}

		@media screen and (max-width: $screen-large) {
	    	&:nth-child(n+6) {
	    		display: none;
	    	}
	    }

	    @media screen and (max-width: 991px) {
	    	&:nth-child(n+5) {
	    		display: none;
	    	}
	    }

	    @media screen and (max-width: $screen-medium) {
	    	&:nth-child(n+6) {
	    		display: none;
	    	}
	    }

	    @media screen and (max-width: $screen-small) {
	    	&:nth-child(n+5) {
	    		display: none;
	    	}
	    }

	    @media screen and (max-width: $screen-xxsmall) {
	    	&:nth-child(n+5) {
	    		display: none;
	    	}
	    }

	    &.swatch-selected, &:hover {
	    	border-color: stencilColor("color-textBase");
	    }
	}
	.card_swatch {
		/*border: 1px solid #a6a6a6;
	    margin: auto 3px 3px;
	    padding: 2px;*/
	    height: 18px;
	    width: 18px;
	    border-radius: 50%;
	    background-size: cover;

	    &.multi_options{
	    	display: flex;
	    	overflow: hidden;
	    	/*border-radius: 5px;*/
	    	span{
	    		border-radius: 0;
	    		/*&:first-child{
	    			border-top-left-radius: 50px;
	    			border-bottom-left-radius: 50px;
	    		}
	    		&:last-child{
	    			border-top-right-radius: 50px;
	    			border-bottom-right-radius: 50px;
	    		}*/
	    	}
	    }

	    span{
	    	width: 100%;
	    	height: 100%;
	    	display: block;
	    	border-radius: 50%;
	    	background-size: cover;
	    }

	    @include breakpoint("small") {
	    	height: 14px;
	    	width: 14px;
	    	/*margin: auto 2px 3px;*/
	    }

	    @include breakpoint("medium") {
	    	height: 18px;
	    	width: 18px;
	    }

	    @include breakpoint(1367px) {
	    	height: 19px;
	    	width: 19px;
	    }

	    @include breakpoint(1601px) {
	    	height: get-height(stencilString("swatch_option_size"));
	    	width: get-width(stencilString("swatch_option_size"));
	    }
	}
	.lable-more-card {
		/*color: stencilColor("color-textSecondary");*/
		color: stencilColor("card-text-color");
		font-size: 12px;
		display: inline-block;
    	vertical-align: super;
    	margin-left: 2px;

    	&:hover, &:focus {
    		color: stencilColor("card-text-hover-color");
    		/*color: stencilColor("color-textSecondary--hover");*/
    	}
	}
}

.card-stock {
	font-size: fontSize("tiny");
    line-height: normal;
    color: stencilColor("card-stock-color");

    .gridView .productGrid & {
    	font-weight: $fontWeight-medium;
    }

    &:empty {
    	margin-bottom: 0;
    }
}

.card-review {
	font-size: 12px;
	color: stencilColor("card-text-color");
}

.card-figcaption-buttons {
    position: absolute;
    top: 10px;
    right: 15px;
    pointer-events: all;
    z-index: 99;
	.quickview{
		@media screen and (max-width: 768px) {
			display: none;
		}
	}
    .card-figcaption-button {
		padding: 10px;
	    border-radius: 50%;
	    line-height: 35px;
	    height: 35px;
	    width: 35px;
	    margin: 5px auto 0;
	    position: relative;
	    -webkit-box-shadow: none;
	    box-shadow: none;
	    overflow: visible;
	    background-color: rgba(stencilColor("card_fig_btn_bg_color"), 0.7);	
	    color: stencilColor("card_fig_btn_text_color");

	    @include breakpoint("small") {
			height: 20px;
	    	width: 20px;
	    	line-height: 20px;
		}

		@include breakpoint("medium") {
			height: 35px;
	    	width: 35px;
	    	line-height: 35px;
		}

		@include breakpoint("large") {
			height: 50px;
	    	width: 50px;
	    	line-height: 50px;
		}

	    &:first-child {
	    	margin: 0 auto;
	    }

	    &:hover, &:focus {
	    	border: none;
	    	background-color: stencilColor("card_fig_btn_bg_hover_color");	
	    	color: stencilColor("card_fig_btn_text_hover_color");

	    	.icon {
	    		fill: stencilColor("button--default-colorHover");
	    		svg {
	    			fill: stencilColor("card_fig_btn_text_hover_color");
	    		}
	    	}
	    }

	    &:active {
	    	background-color: stencilColor("card_fig_btn_bg_hover_color");
	    	color: stencilColor("card_fig_btn_text_hover_color");

	    	.icon {
	    		fill: stencilColor("button--default-colorActive");
	    		svg {
	    			fill: stencilColor("button--default-colorActive");
	    		}
	    	}
	    }

	    .icon {
	    	transform: translate(-50%,-50%);
		    -webkit-transform: translate(-50%,-50%);
		    -moz-transform: translate(-50%,-50%);
		    -ms-transform: translate(-50%,-50%);
		    -o-transform: translate(-50%,-50%);
	    	display: -ms-flexbox;
		    display: flex;
		    margin: auto;
		    -ms-flex-align: center;
		    align-items: center;
		    -ms-flex-pack: center;
		    justify-content: center;
		    position: absolute;
		    top: 50%;
		    left: 50%;
		    right: auto;
		    bottom: auto;
		    width: auto;
		    height: auto;
		    fill: stencilColor("card_fig_btn_text_color");

		    > svg {
		    	fill: stencilColor("card_fig_btn_text_color");
		    	width: 16px;
		    	height: 16px;

		    	@include breakpoint("small") {
					width: 16px;
		    		height: 16px;
				}

				@include breakpoint("medium") {
					width: 18px;
		    		height: 18px;
				}

				@include breakpoint("large") {
					width: 22px;
		    		height: 22px;		
				}
		    }
	    }

	    &[data-tooltip] {
			&::before {
				content: attr(data-tooltip);
				position: absolute;
			    top: 50%;
			    left: -10px;
			    transform: translate(-100%, -50%);
			    -webkit-transform: translate(-100%, -50%);
			    -ms-transform: translate(-100%, -50%);
			    -o-transform: translate(-100%, -50%);
			    -moz-transform: translate(-100%, -50%);
			    padding: 4px 10px;
			    font-size: 12px;
			    line-height: 1.6;
			    white-space: nowrap;
			    visibility: hidden;
			    opacity: 0;
			    pointer-events: none;
			    -webkit-box-shadow: 3px 4px 4px rgba(0, 0, 0, 0.15);
			    box-shadow: 3px 4px 4px rgba(0, 0, 0, 0.15);
			    text-transform: capitalize;
			    background-color: stencilColor("card_fig_btn_bg_color");	
			    color: stencilColor("card_fig_btn_text_color");
			}

			&::after {
				content: "";
				position: absolute;
			    top: 50%;
			    left: -12px;
			    transform: translateY(-50%);
			    -webkit-transform: translateY(-50%);
			    -ms-transform: translateY(-50%);
			    -o-transform: translateY(-50%);
			    -moz-transform: translateY(-50%);
			    border: none;
			    border-top: 6px solid transparent;
			    border-bottom: 6px solid transparent;
			    font-size: 0;
			    width: 0;
			    line-height: 0;
			    visibility: hidden;
			    opacity: 0;
			    pointer-events: none;
			    right: auto;
			    background: none;
			    z-index: 1;
			    height: auto;
			    bottom: auto;
			    transition: initial;
			    border-left: 6px solid stencilColor("card_fig_btn_bg_color");	
			    color: stencilColor("card_fig_btn_text_color");
			}

			&:hover, &:focus {
				&::before, &::after {
	    			visibility: visible;
	    			opacity: 1;
	    		}
    		}
		}

		input[data-compare-id] {
			@include compareButtonCss;
		}
	}
}


/* Product Cards Style End */

/* Category Page Start */
.category-description {
	margin-bottom: 40px;
	* {
		font-size: $fontSize-root - 2;
		line-height: $fontSize-root * 1.75;

		@include breakpoint("medium") {
			font-size: $fontSize-root;
		}

		@include breakpoint("large") {
			font-size: $fontSize-root + 2;
		}
	}
}

.subcategories {
	list-style: none;
	display: flex;
	justify-content: flex-start;
	flex-wrap: wrap;
	margin: 0 -10px 20px;

	@include breakpoint("small") {
		margin: 0 -10px 20px;
	}

	@include breakpoint("medium") {
		margin: 0 -10px 30px;
	}

	@include breakpoint("large") {
		margin: 0 -10px 60px;
	}

	.subcat {
		width: 50%;
		flex: 0 1 50%;
		margin-bottom: 30px;
		text-align: center;
		padding-left: 10px;
		padding-right: 10px;
		display: flex;
	    flex-direction: column;
	    justify-content: space-between;

		@include breakpoint("small") {
			width: 50%;
			flex: 0 1 50%;
		}

		@include breakpoint(768px) {
			width: calc(100% / 3);
			flex: 0 1 calc(100% / 3);
		}

		@include breakpoint("medium") {
			width: calc(100% / 3);
			flex: 0 1 calc(100% / 3);
		}

		@include breakpoint("large") {
			width: calc(100% / 3);
			flex: 0 1 calc(100% / 3);
		}

		@include breakpoint(1366px) {
			width: 25%;
			flex: 0 1 25%;		
		}

		.subcat-img {
			display: flex;
			margin-bottom: 15px;
			position: relative;
			overflow: hidden;
			object-fit: cover;
			flex: 1 0 auto;

			img {
				-webkit-transition: all 0.3s ease;
				-moz-transition: all 0.3s ease;
				transition: all 0.3s ease;
				width: 100%;
				object-fit: cover;
				max-height: inherit;
				object-position: center center;
			}

			&:hover, &:focus {
				img {
					-webkit-transform: scale(1.1);
					-moz-transform: scale(1.1);
					-ms-transform: scale(1.1);
					-o-transform: scale(1.1);
					transform: scale(1.1);
				}
			}

			@include breakpoint("medium") {
				max-height: 270px;
				min-height: 270px;
			}
		}

		.subcat-name {
			display: block;
			font-size: stencilNumber("section-sub-fontsize") - 2;
			text-decoration: none;
			font-weight: $header-font-weight;
			font-family: $header-font-family;
			color: stencilColor("subcat-text-color");

			&:hover{
				color: stencilColor("subcat-text-hover-color");
			}

			@include breakpoint("medium") {
				font-size: stencilNumber("section-sub-fontsize") - 1;
			}

			@include breakpoint("large") {
				font-size: stencilNumber("section-sub-fontsize");
			}
		}
	}
}

.actionBar-wrapper {
