.listItem-buttons {
    position: absolute;
    top: 10px;
    right: 15px;
    pointer-events: all;
    z-index: 99;
	.listItem-figureBody{
		.quickview{
			@media screen and (max-width: 768px) {
				display: none;
			}
		}
	}
    .listItem-button {
		padding: 17px 17px;
	    border-radius: 50%;
	    line-height: 20px;
	    height: 20px;
	    width: 20px;
	    margin: 5px auto 0;
	    position: relative;
	    -webkit-box-shadow: none;
	    box-shadow: none;
	    border: none;
	    overflow: visible;
		font-size: 10px;
		background-color: rgba(stencilColor("card_fig_btn_bg_color"), 0.7);	
	    color: stencilColor("card_fig_btn_text_color");

	    @include breakpoint("small") {
			height: 20px;
	    	width: 20px;
	    	line-height: 20px;
	    	padding: 17px 17px;
		}

		@include breakpoint("medium") {
			height: 35px;
	    	width: 35px;
	    	line-height: 35px;
	    	padding: 17px 17px;
			font-size: 14px;
		}

		@include breakpoint("large") {
			height: 50px;
	    	width: 50px;
	    	line-height: 50px;
	    	padding: 17px 22px;
		}

	    &:hover, &:focus {
	    	border: none;
	    	background-color: stencilColor("card_fig_btn_bg_hover_color");
	    	color: stencilColor("card_fig_btn_text_hover_color");

	    	.icon {
	    		fill: stencilColor("button--default-colorHover");
	    		svg {
	    			fill: stencilColor("button--default-colorHover");
	    		}
	    	}
	    }

	    &:active {
	    	background-color: stencilColor("card_fig_btn_bg_hover_color");
	    	color: stencilColor("card_fig_btn_text_hover_color");

	    	.icon {
	    		fill: stencilColor("button--default-colorActive");
	    		svg {
	    			fill: stencilColor("button--default-colorActive");
	    		}
	    	}
	    }

	    .icon {
	    	transform: translate(-50%,-50%);
		    -webkit-transform: translate(-50%,-50%);
		    -moz-transform: translate(-50%,-50%);
		    -ms-transform: translate(-50%,-50%);
		    -o-transform: translate(-50%,-50%);
	    	display: -ms-flexbox;
		    display: flex;
		    margin: auto;
		    -ms-flex-align: center;
		    align-items: center;
		    -ms-flex-pack: center;
		    justify-content: center;
		    position: absolute;
		    top: 50%;
		    left: 50%;
		    right: auto;
		    bottom: auto;
		    width: auto;
		    height: auto;
		    fill: stencilColor("button--default-color");

		    > svg {
		    	fill: stencilColor("button--default-color");
		    	width: 16px;
		    	height: 16px;

		    	@include breakpoint("small") {
					width: 16px;
		    		height: 16px;
				}

				@include breakpoint("medium") {
					width: 18px;
		    		height: 18px;
				}

				@include breakpoint("large") {
					width: 22px;
		    		height: 22px;		
				}
		    }
	    }

	    &[data-tooltip] {
			&::before {
				content: attr(data-tooltip);
				position: absolute;
			    top: 50%;
			    left: -10px;
			    transform: translate(-100%, -50%);
			    -webkit-transform: translate(-100%, -50%);
			    -ms-transform: translate(-100%, -50%);
			    -o-transform: translate(-100%, -50%);
			    -moz-transform: translate(-100%, -50%);
			    padding: 4px 10px;
			    font-size: 12px;
			    line-height: 1.6;
			    white-space: nowrap;
			    visibility: hidden;
			    opacity: 0;
			    pointer-events: none;
			    -webkit-box-shadow: 3px 4px 4px rgba(0, 0, 0, 0.15);
			    box-shadow: 3px 4px 4px rgba(0, 0, 0, 0.15);
			    text-transform: capitalize;
			    background-color: stencilColor("card_fig_btn_bg_color");	
			    color: stencilColor("card_fig_btn_text_color");
			}

			&::after {
				content: "";
				position: absolute;
			    top: 50%;
			    left: -12px;
			    transform: translateY(-50%);
			    -webkit-transform: translateY(-50%);
			    -ms-transform: translateY(-50%);
			    -o-transform: translateY(-50%);
			    -moz-transform: translateY(-50%);
			    border: none;
			    border-top: 6px solid transparent;
			    border-bottom: 6px solid transparent;
			    font-size: 0;
			    width: 0;
			    line-height: 0;
			    visibility: hidden;
			    -webkit-opacity: 0;
				-moz-opacity: 0;
			    opacity: 0;
			    pointer-events: none;
			    right: auto;
			    background: none;
			    z-index: 1;
			    height: auto;
			    bottom: auto;
			    transition: initial;
			    border-left: 6px solid stencilColor("card_fig_btn_bg_color");	
			    color: stencilColor("card_fig_btn_text_color");
			}

			&:hover, &:focus {
				&::before, &::after {
	    			visibility: visible;
	    			-webkit-opacity: 1;
					-moz-opacity: 1;
	    			opacity: 1;
	    		}
    		}
		}

		input[data-compare-id] {
			@include compareButtonCss;
		}
	}

	.listItem-figureBody:first-child {
		.listItem-button {
			margin: 0 auto;
		}
    }
}

