.add-wishlist--template {
	.wishlist-form {
		.form-field {
			@media screen and (max-width: $screen-medium) {
				padding-left: 0;
				padding-right: 0;
			}
		}
	}
}

.wishlist--template {
	.productGrid {
		.product {
			form {
				text-align: center;
			}
		}

		.card {
			margin-bottom: 14px;
		}
	}
}

.createaccount--template {
	.form-field {
		.form-select {
			background-position: right 8px top 12.5px;
		}
	}

	.account {
		@media screen and (max-width: $screen-xxsmall) {
			overflow: auto;
		}
	}
}

.panel--address {
	.panel-body {
		.form-actions {
			.button {
				padding: 12px 25px;
			}
		}
	}
}

.table-actions {
	.button {
		padding-left: 20px;
		padding-right: 20px;
		margin: 0 0 5px;
		display: block;
		width: 100%;

		@include breakpoint("medium") {
			padding-left: 20px;
			padding-right: 20px;
			margin: 5px;
			display: inline-block;
			width: auto;
		}

		@include breakpoint("large") {
			padding-left: 42px;
			padding-right: 42px;
		}

		& + .button {
			margin: 0 0 5px;

			@include breakpoint("medium") {
				margin: 5px;
			}
		}
	}
}

.createaccount_thanks--template {
	.page-heading {
		text-align: center;
	}
}

/* Account Pages End */


/* Cart Page Start */

.cart-item-quantity{
	.form-increment {
	    border: 1px solid #D7D7D7;
	    margin: 0;

	    .button {
	        border: none;
	        padding: 8px;
	        background-color: stencilColor("color-white");

	        .icon {
	            fill: stencilColor("icon-color");
	        }

	        &[data-action="dec"] {
	            border-right: 1px solid #d7d7d7;
	        }

	        &[data-action="inc"] {
	            border-left: 1px solid #d7d7d7;
	        }

	        &:hover, &:focus {
	            .icon {
	                fill: stencilColor("icon-color-hover");
	            }
	        }
	    }
	}
}

.shipping-quotes {
	margin-bottom: 14px;
	text-align: right;
    clear: both;
}

.shipping-estimate-submit {
	margin-bottom: 0;
}

.estimator-form {
    dl {
        margin-bottom: 7px;
    }
}

.cart-total-label, .cart-total-value {
	@media screen and (max-width: $screen-medium) {
        padding: 14px 0;
        display: block;
    }
}

.cart-total-label {
	@media screen and (max-width: $screen-medium) {
        text-align: left;
        width: 50%;
	}
}

.cart-total-value {
	@media screen and (max-width: $screen-medium) {
        text-align: right;
        width: 50%;
    }
}

.cart-total + .cart-total {
	@media screen and (max-width: $screen-medium) {
    	border-top: container("border");
    }
}

.coupon-form, .cart-gift-certificate-form {
	@media screen and (max-width: $screen-medium) {
    	.form-input {
    		width: 73%;
    	}

    	.button {
    		width: 25%;
    	}
	}
}

.cart-item-info {
	@media screen and (max-width: $screen-medium) {
		margin-bottom: 7px;
	}

	&.cart-item-quantity {
		@media screen and (max-width: $screen-small) {
			.cart-item-label {
				margin-top: 7px;
				margin-right: 21px;
			}
		}
	}
}

.cart--template {
	.container {
		position: static;
	}

	.body {
		position: relative;
		padding-bottom: 84px;
		margin-bottom: 0;
		margin-top: 0;
		padding-top: 35px;

		@include breakpoint("medium") {
	        padding-top: 35px;
	        margin-top: 0;
	    }
	}
}

/* Cart Page End */



/* Marketing Banner Visible Start */

.default--template {
	&.marketing-banner-visible {
		.transparent-header {
			top: 0;
			animation: none;

			.heroCarousel {
				.slick-next, .slick-prev {
					@media screen and (max-width: 551px) {
						top: 33%;
					}
				}
			}

			&.sticky {
				top: 0;
				animation: none;
			}
		}

		.heroCarousel {
			@media screen and (max-width: 801px) {
				margin-top: 0;
			}
		}
	}

	.transparent-header {
		animation: none;
		&.sticky {
			animation: none;
			position: fixed;
		}
	}
} 

.heroCarousel.heroCarousel-content--transparent .heroCarousel-content{
	@media screen and (max-width: 550px) {
		position: relative;
	}
	&::before{
		@media screen and (max-width: 550px) {
			content: "";
			display: block;
			background:  stencilColor("carousel-content-mobile-transparent-bg");
			opacity: 0.3;
			position: absolute;
			top: 0;
			right: 0;
			left: 0;
			bottom: 0;
			z-index: -1;	
		}	
	}
}
/* Marketing Banner Visible End */


/* Product Detail Page Start */

.productView-price {
	.price--rrp,
	.price--non-sale,
	.price--discounted {
	    color: stencilColor("pdp-price-color");
	    font-size: fontSize("smaller");

	    @include breakpoint("medium") {
	    	font-size: 20px;
	    }
	}

	.price--withoutTax {
	    color: stencilColor("pdp-price-sale-color");    
	}
}

.related-products-section, .similiar-products-section {
	.products-title {
		text-transform: uppercase;
		font-size: 20px;
	    color: $header-font-color;
	    font-weight: $header-font-weight;
	    font-family: $fontFamily-headings;
	    margin: 0 auto 20px;
	    text-align: center;

	    @include breakpoint("medium") {
			font-size: 32px;
			margin: 0 auto 35px;
		}
	}

	.productCarousel {
		margin-left: -10px;
		margin-right: -10px;
		display: inline-block;

		@include breakpoint("large") {
			margin-left: -20px;
			margin-right: -20px;
		}

		.slick-next {
		    &::before {
		        @include carousel-arrow-color( $slick-arrow-color-hover, "next");
		    }

		    &:hover {
		        &::before {
		            @include carousel-arrow-color( $slick-arrow-color-hover, "next");
		        }
		    }
		}
		.slick-prev {
		    &::before {
		        @include carousel-arrow-color( $slick-arrow-color-hover, "prev");
		    }

		    &:hover {
		        &::before {
		            @include carousel-arrow-color( $slick-arrow-color-hover, "prev");
		        }
		    }
		}
	}

	.productCarousel-slide {
		padding-right: 10px;
		padding-left: 10px;

		@include breakpoint("large") {
			padding-right: 20px;
			padding-left: 20px;
		}
	}
}

.related-products-section {
	margin-bottom: 40px;

	@include breakpoint("large") {
		margin-bottom: 70px;
	}

	#related-products {
		width: 100%;
	    margin-left: auto;
	    margin-right: auto;
	    max-width: 100%;
	    text-align: center;
	}
}

.similiar-products-section {
	#similiar-products {
		width: 100%;
	    margin-left: auto;
	    margin-right: auto;
	    max-width: 100%;
	    text-align: center;
	}
}

.product--template {
	.body {
		margin-bottom: 50px;
		margin-top: 18px;
	}
}

.productDetailsView-section:not(:last-child) {
	margin-bottom: 30px;

	@include breakpoint("large") {
		margin-bottom: 50px;
	}

	&.product-description {
		.productView-description {
			display: block;
	    	p {
	            font-size: 16px;
	            line-height: 28px;
	        }
        }
    }
}

.productView-description {
	.tab-content {
		margin-bottom: 30px;
		padding: 20px 0 0;

		@include breakpoint("large") {
			margin-bottom: 50px;
		}

		&#tab-description {
			p {
	            font-size: 16px;
	            line-height: 28px;
	        }
    	}

    	.toggle {
    		border: none;
    	}
	}
}

.productDetailsView-section {
	clear: both;
}

.productView-description {

	.productView-title {
		.icon {
			position: absolute;
			right: 0;
			top: 50%;
			transform: translate(0, -50%);
			width: 20px;
			height: 20px;
			fill: $header-font-color;
		}

        .minus-icon {
            display: none;
        }

        &.toggle-active {
            .plus-icon {
                display: none;
            }

            .minus-icon {
                display: block;
            }
        }
    }

    .productView-description {
    	.productReviews {
    		margin-bottom: 0;
    		border-top: none;
    	}
    }
}

.productView-rating {
	margin-bottom: 18px;

	.icon--ratingEmpty {
		svg {
			fill: stencilColor("color-textSecondary");
		}
	}

	span[role="img"] {
		margin-right: 14px;

		& + span {
			/*color: stencilColor("color-textSecondary");*/
			color: stencilColor("brand-text-color");
			margin-right: 14px;
		}
	}

	#productReview_link {
		/*color: stencilColor("color-textSecondary");*/
		color: stencilColor("brand-text-color");
		margin-right: 14px;
	}

	.icon {
		width: 13px;
		height: 13px;
	}
}

.slick-vertical {
	.slick-slide {
		border: none;
	}
}

.product--template {
	ol.breadcrumbs {
		margin-bottom: 20px;
	}
}

.tabs {
    padding: 0;
    margin: 0 auto 5px;
    text-align: center;
    border: none;

    @include breakpoint("medium") {
        margin: 0 auto 25px;
    }
}

.writeReview-form {
	.form-select {
		background-position: right 8px top 12.5px;
	}
}

.productView-img-container::after{
	padding-bottom: 100%;
}

/* Product Detail Page End */


/* Gift Certificate Start */

.gift-certificate-form {
	.form-select {
		background-position: right 8px top 12.5px;
	}

	.form-row {
        &:nth-child(3) {
            margin-bottom: 14px;
            .form-fieldset {
            	&:first-child {
            		.form-field {
            			padding-left: 0;
                		padding-right: 0;
            		}
            	}

                &:last-child {
                    > div:nth-child(1) {
                    	padding-left: 0;
                		padding-right: 0;
                    }
                }
            }
        }
    }
}

#gift-certificate-balance {
	.button {
		padding-top: 13px;
		padding-bottom: 13px;
	}
}

#gift-certificate-submit {
	@media screen and (max-width: $screen-small) {
		padding-right: 0;
		padding-left: 0;
	}
}

/* Gift Certificate End */


/* Brand Page Start */

.brand {
	.card-figure {
		background: none;
		overflow: hidden;

		.card-image {
			width: 100%;
		    -o-object-fit: cover;
		    object-fit: cover;
		    -webkit-transition: all 0.5s ease;
		    -moz-transition: all 0.5s ease;
		    transition: all 0.5s ease;
		}

		&:hover, &:focus {
			.card-image {
				-webkit-transform: scale(1.1);
				-moz-transform: scale(1.1);
				transform: scale(1.1);
			}
		}
	}
}

/* Brand Page End */


/* 404 Page Start */

#search_query_adv {
	+ .button {
		padding-top: 13px;
		padding-bottom: 13px;
	}
}

/* 404 Page End */


/* Blog Listing Page Start */
.blog-wrapper{
	display: flex;
	align-items: flex-start;
	flex-wrap: wrap;
	@include breakpoint("medium"){
		margin: 0 -10px;
	}
	.blog{
		width: 100%;
		padding: 0;
		border: none;
		@include breakpoint("medium"){
			margin: 0 10px 2.5rem;
			width: calc(50% - 20px);
		}
		@include breakpoint("large"){
			width: calc(33.33% - 20px);
		}
		.blog-post-figure{
			.blog-thumbnail{
				a{
					display: block;
					overflow: hidden;
					img {
						width: 100%;
						object-fit: cover;
						transition: all 0.5s ease;
					}
				}
			}
			&:hover, &:focus {
				.blog-thumbnail{
					a{
						img {
							-webkit-transform: scale(1.1);
							-moz-transform: scale(1.1);
							transform: scale(1.1);
						}
					}
				}
			}
		}
		.blog-post-body{
			.blog-header{
				p{
					margin-bottom: 5px;
				}
			}

			.blog-post {
				a {
					color: stencilColor("color-textLink--hover");
					display: block;
					text-transform: capitalize;
					font-weight: 700;
					margin-top: 10px;

					&:hover, &:focus {
						color: stencilColor("color-textLink");
					}
				}
			}
		}
	}
}
/* Blog Listing Page End */


/* Blog Detail Page Start */

.blog_post--template {
	ol.breadcrumbs {
		margin: 0 auto 14px;
		max-width: 980px;
	}

	.blog {
		max-width: 980px;

		&.show-blog-img {
			.blog-post-figure {
				display: block;
			}
		}
	}

	.blog-post-body {
		width: 100%;
		text-align: left;
	}

	.blog-date, .blog-author {
		text-align: left;
	}

	.blog-title {
		margin-left: 0;
		margin-right: 0;
	}
}

/* Blog Detail Page End */


/* Variant Based Style Start */
body.light {
	#newsletter-popup {
		#dialog {
			box-shadow: 1px 2px 10px rgba(0, 0, 0, 0.15);
		}
	}
	.navPages-container{
		.navPages{
			.navPages-list{
				.navPages-item{
					.navPage-subMenu{
						box-shadow: 1px 2px 3px rgba(0, 0, 0, 0.15);

						@media screen and (max-width: $screen-medium) {
							box-shadow: none;
						}
					}
				}
			}
		}
	}
	.footer-info-list{
		.view-all{
			a{
				font-weight: 400;
				text-decoration: underline;
			}
		}
	}
	.facetedSearch-refineFilters{
		.sidebarBlock-heading [data-faceted-search-facet]{
			color: #bf645c;
			&:hover, &:focus {
		        color: stencilColor("color-textSecondary--hover");
		    }
		}
	}
	.facetedSearch-clearLink{
		color: #bf645c;
		&:hover, &:focus {
	        color: stencilColor("color-textSecondary--hover");
	    }
	}
	.footer-info-col[data-section-type=newsletterSubscription]{
		.form-prefixPostfix{
			.form-input{
				background-color: stencilColor("color-white");
			}
		}
	}

	.navPages-container {
		@media screen and (max-width: $screen-medium) {
			border-right: 1px solid #e2e2e2;
		}
		.navPages {
			.navPages-list {
				.navPages-item {
					.navPage-subMenu {
						.navPage-subMenu-list {
							.navPage-subMenu-item {
								.navPage-childList {
									border-color: #e8e8e8;
								}
							}
						}
					}
				}
			}
		}
	}

	.dropdown--quickSearch {
		border-top-color: #ebebeb;
	}

	.header {
		.header-wrapper {
			.header-stacked {
				width: calc(100% - 64px);
			}

			.header-wrapper-inner {
				.header-left {
					@media screen and (max-width: $screen-medium) {
						width: calc(100% - 64px);
					}
				}
			}
		}

		&.header-left {
			.navPages-container {
				@media screen and (max-width: $screen-medium) {
					border-left: 1px solid #e2e2e2;
					border-right: none;
				}
			}

			.header-wrapper {
				.header-wrapper-inner {
					.header-middle {
						width: calc(100% - 64px);
					}
				}
			}
		}
	}

	.accordion--navList{
		.accordion-title{
			font-weight: 400;
		}
	}

}
/* Variant Based Style End */


/* Product Detail Video Gallery Start */

.videoGallery-main {
	padding-bottom: 576px;

	@media screen and (max-width: 1023px) {
		padding-bottom: 56.25%;
	}

	iframe, object, embed, video {
		height: 576px;
    	width: 1024px;

		@media screen and (max-width: 1023px) {
			width: 100%;
			height: 100%;
		}
	}
}

/* Product Detail Video Gallery End */

