.header.light-bg-header{
	background-color: rgba(0, 0 , 0 ,0.4);
	&.sticky{
		background-color: stencilColor("header-backgroundColor");
	}
	&.transparent-header{
		background-color: rgba(0, 0 , 0 ,0.4);
		&.sticky{
			background-color: stencilColor("header-backgroundColor");
		}
	}
}
.heroCarousel{
	margin-top: 0;
}
.header-logo-image.transparent-header-logo-image{
	display: none;
}
.transparent-header{
