pages/home:
---
products:
    new:
        limit: {{theme_settings.homepage_new_products_count}}
    featured:
        limit: {{theme_settings.homepage_featured_products_count}}
    top_sellers:
        limit: {{theme_settings.homepage_top_products_count}}
carousel: {{theme_settings.homepage_show_carousel}}
blog:
    recent_posts:
        limit: {{theme_settings.homepage_blog_posts_count}}
---
{{#partial "hero"}}
    {{{region name="home_below_menu"}}}
    {{{region name="home_above_carousel"}}}
    {{#and carousel carousel.slides.length}}
        {{> components/carousel arrows=theme_settings.homepage_show_carousel_arrows play_pause_button=theme_settings.homepage_show_carousel_play_pause_button}}
    {{/and}}
    {{{region name="home_below_carousel"}}}
{{/partial}}

{{#partial "page"}}

    {{#each shipping_messages}}
        {{> components/common/alert/alert-info message}}
    {{/each}}
</div>
<div class="main full">

    {{> components/custom/homepage-text-section}}

    {{#if theme_settings.variant_name '===' 'light'}}
        {{> components/custom/homepage-products-section}}
        {{{region name="home_below_products"}}}
    {{/if}}

    {{> components/custom/homepage-sidebyside-banners-section}}

    {{> components/custom/homepage-categories-section}}

    {{#or (if theme_settings.variant_name '===' 'vintage') (if theme_settings.variant_name '===' 'bold')}}
        {{> components/custom/homepage-products-section}}
    {{/or}}

    {{> components/custom/homepage-horizontal-banners}}

    {{> components/custom/homepage-parallax-banner}}

    {{> components/custom/homepage-recent-blogs}}

    {{> components/custom/homepage-newsletter-popup}}

</div>
{{/partial}}
{{> layout/base}}

<script>
    new universalParallax().init();
</script>

components/custom/homepage-text-section:
